from django.conf.urls import url, includefrom rest_framework import routersfrom comprobante.views.comprobante import ComprobanteViewSet,ComprobanteSaveViewSetfrom comprobante.models.comprobante import Comprobantefrom comprobante.views.voleta import Voleta,VoletaDetailrouter = routers.DefaultRouter()router.register(r'comprobante', ComprobanteViewSet)router.register(r'comprobante-save', ComprobanteSaveViewSet,base_name=Comprobante)urlpatterns = [    url(r'^', include(router.urls)),    url(r'^voleta/$', Voleta),    url(r'^voletapdf/(?P<pk>[^/.]+)/$', VoletaDetail.as_view()),]