from django.db import modelsfrom catalogo.models.persona import Personafrom uuid import uuid4class Reserva(models.Model):    id = models.UUIDField(primary_key=True, default=uuid4, editable=False)    costo_total=models.FloatField(null=True,blank=True)    estado = models.BooleanField(default=True)    persona = models.ForeignKey(Persona)    created_at = models.DateTimeField(auto_now_add=True,null=True,blank=True)    updated_at = models.DateTimeField(auto_now=True,null=True,blank=True)    class Meta:        verbose_name = "Reserva"        verbose_name_plural = "Reservas"