from django.conf.urls import url, includefrom rest_framework import routersfrom reserva.views.reserva import ReservaViewSetfrom reserva.views.reserva_detalle import DetalleReservaViewSetfrom reserva.models.detalle_reserva import DetalleReservafrom reserva.views.reserva_detalle import DetalleReservaViewSet,DetalleFilterReservaViewSetrouter = routers.DefaultRouter()router.register(r'reserva', ReservaViewSet)router.register(r'reserva', DetalleReservaViewSet)router.register(r'reserva-filter', DetalleFilterReservaViewSet)router.register(r'reserva-detalle', DetalleReservaViewSet,base_name=DetalleReserva)urlpatterns = [    url(r'^', include(router.urls)),]