from rest_framework import serializers, viewsetsfrom reserva.models.reserva import Reservaimport logginglog = logging.getLogger(__name__)class ReservaSerializer(serializers.ModelSerializer):    class Meta:        model = Reserva        fields = '__all__'class ReservaViewSet(viewsets.ModelViewSet):    queryset = Reserva.objects.all()    serializer_class = ReservaSerializer